import 'package:flutter/material.dart'; import 'package:firebase_database/firebase_database.dart'; class SearchData extends StatefulWidget { @override _SearchDataState createState() => _SearchDataState(); } class _SearchDataState extends State<SearchData> { final TextEditingController _searchQuery = new TextEditingController(); List<String> _list; bool _isSearching; String _searchText = ""; @override void initState() { super.initState(); _isSearching = false; _list = List(); _list.addAll(["One", "Two", "Three", "Four", "Five"]); } void _handleSearchStart() { setState(() { _isSearching = true; }); } void _handleSearchEnd() { setState(() { this._searchQuery.clear(); _isSearching = false; }); } Widget _buildList() { if (_isSearching) { return ListView.builder( itemCount: _list.length, itemBuilder: (context, i) { final String name = _list[i]; if (name.toLowerCase().contains(_searchText.toLowerCase())) { return ListTile( title: Text( name, ), onTap: () {}, leading: Icon(Icons.work), ); } else { return Container(); } }, ); } else { return Container(); } } @override Widget build(BuildContext context) { return Scaffold( appBar: AppBar( title: Text("Search Data"), ), body: Container( child: Column( children: <Widget>[ Container( child: TextField( onChanged: (string) { _handleSearchStart(); }, controller: _searchQuery, decoration: InputDecoration( labelText: "Search", prefixIcon: Icon(Icons.search), border: OutlineInputBorder( borderRadius: BorderRadius.all(Radius.circular(25.0),), ) ), ), ), Expanded( child: _buildList(), ), ], ), ), ); } }